const g=localStorage.getItem("flujo-language")||"es";document.documentElement.lang=g;function m(){localStorage.getItem("flujo-language")}m();const c=document.getElementById("navbar");window.addEventListener("scroll",()=>{window.scrollY>50?c?.classList.add("scrolled"):c?.classList.remove("scrolled")});const n=document.getElementById("mobile-menu-toggle"),l=document.getElementById("mobile-menu");n?.addEventListener("click",()=>{n.classList.toggle("active"),l?.classList.toggle("show"),document.body.style.overflow=l?.classList.contains("show")?"hidden":""});const u=document.querySelectorAll(".mobile-nav-link");u.forEach(e=>{e.addEventListener("click",()=>{n?.classList.remove("active"),l?.classList.remove("show"),document.body.style.overflow=""})});document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(o){o.preventDefault();const t=document.querySelector(this.getAttribute("href")||"");if(t){const i=t.offsetTop-80;window.scrollTo({top:i,behavior:"smooth"})}})});const a=document.getElementById("lang-toggle"),r=document.getElementById("lang-dropdown"),v=document.getElementById("current-lang"),d=document.querySelectorAll(".lang-option");let s=localStorage.getItem("flujo-language")||"es";v.textContent=s.toUpperCase();d.forEach(e=>{e.dataset.lang===s&&e.classList.add("active")});a?.addEventListener("click",e=>{e.stopPropagation(),a.classList.toggle("active"),r?.classList.toggle("show")});document.addEventListener("click",()=>{a?.classList.remove("active"),r?.classList.remove("show")});d.forEach(e=>{e.addEventListener("click",o=>{o.stopPropagation();const t=e.dataset.lang;t&&t!==s&&(localStorage.setItem("flujo-language",t),window.location.reload())})});
